class Node{
    constructor(data){
        this.data=data
        this.next=null
    }
}
class LinkedList{
    constructor(){
        this.head=null
    }
    fromArray(array){
        if(array.length==0){
            return null
        }
        this.head=new Node(array[0])
        let current=this.head
        for(let i=1;i<array.length;i++){
            current.next=new Node(array[i])
            current=current.next
        }
    }
    deleteFirst(){
       
        //if no head create a LL
        if(!this.head){
            console.log("List is empty!");
        return
        }
       this.head=this.head.next//whateer in head if its null or link
    }
    deleteLast(){
        if(!this.head){
            console.log("List is empty!");
            return 
        }
        //[1,l]->[5,l]-->[9,l]->[8,null]
        //head--------->current
        let temp=this.head
        while(temp.next && temp.next.next){
            temp=temp.next
        }
        //now we have reached secont last node 
        temp.next=null
    }
    deleteByValue(value){
        if(!this.head){
            return 
        }
       
        if(this.head.data==value){
            this.head=this.head.next
            return
        }
         //reach the node just before that value
         let temp=this.head
         //[1,l]->[5,l]-->[9,l]->[8,null]
        while(temp.next){
            if(temp.next.data==value){
                //change link of current node to node after next node
             temp.next=temp.next.next
             return
            }
            temp=temp.next
        }
        if(temp.next==null){
            console.log("value not found");
            
        }
        
    }
    printList() {
        let current = this.head;
        let output = "";
        while (current) {
            output += current.data + " -> ";
            current = current.next;
        }
        output += "null";
        console.log(output);
    }
    deleteByIndex(index){
        if(index<0 && !this.head){
            return
        }
        if(index==0){
            this.head=this.head.next
           































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































































            













































































































































































































































































































































































        }
       
        let prev=null
        let current=this.head
        let currentIdx=0
        while(current.next && currentIdx<index){
            prev=current
            current=current.next
            currentIdx++
        }
        if(currentIdx!=index){
            console.log("index not in bound");
            return
        }
        prev.next=current.next
        current.next=null
    }

}
